mapper.insert:
/* 게시글 작성 */
INSERT INTO BOARD
    (
        BOARD_NO
      , TITLE
      , CONTENT
      , WRITER
      , CREATE_DATE
    )
VALUES
    (
        BOARD_NO_SEQ.NEXTVAL
      , #{title}
      , #{content}
      , #{writer}
      , SYSDATE
    )

mapper.update:
/* 게시글 수정 */
UPDATE BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
     , UPDATE_DATE = SYSDATE
 WHERE BOARD_NO = #{boardNo}

/* 게시글 삭제 */
UPDATE BOARD
   SET USE_YN = 'Y'
     , UPDATE_DATE = SYSDATE
 WHERE BOARD_NO = #{boardNo}
