mapper.select:
/* 게시글 목록 조회 */
SELECT T1.no , T1.boardNo , T1.title , T1.content , T1.writer , T1.createDate , T1.updateDate FROM (SELECT /*+ INDEX_DESC(BOARD BOARD_PK)*/ rownum AS no , BOARD_NO AS boardNo , TITLE AS title , CONTENT AS content , WRITER AS writer , CREATE_DATE AS createDate , UPDATE_DATE AS updateDate , USE_YN FROM BOARD WHERE 1=1 AND USE_YN = 'Y'
AND rownum <= #{pageNum} * #{amount} )T1 WHERE no > (#{pageNum} -1) * #{amount}

mapper.insert:

    SELECT BOARD_NO_SEQ.NEXTVAL FROM DUAL

/* 게시글 작성 */
INSERT INTO BOARD
    (
        BOARD_NO
      , TITLE
      , CONTENT
      , WRITER
      , CREATE_DATE
    )
VALUES
    (
        #{boardNo}
      , #{title}
      , #{content}
      , #{writer}
      , SYSDATE
    )

mapper.update:
/* 게시글 수정 */
UPDATE BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
     , UPDATE_DATE = SYSDATE
 WHERE BOARD_NO = #{boardNo}

/* 게시글 삭제 */
UPDATE BOARD
   SET USE_YN = 'N'
     , UPDATE_DATE = SYSDATE
 WHERE BOARD_NO = #{boardNo}
