mapper.insert:
/* 파일정보 저장 */
INSERT INTO FILE_INFO
    (
        FILE_NO
      , BOARD_NO
      , ORG_FILE_NAME
      , FILE_PATH
      , FILE_SIZE
      , CREATE_DATE
    )
VALUES
    (
        FILE_NO_SEQ.NEXTVAL
      , #{boardNo}
      , #{orgFileName}
      , #{filePath}
      , #{fileSize}
      , SYSDATE
    )

mapper.update:
/* 게시글 수정 */
UPDATE FILE_INFO
   SET ORG_FILE_NAME = #{orgFileName}
     , FILE_PATH = #{filePath}
     , FILE_SIZE = #{fileSize}
     , UPDATE_DATE = SYSDATE
 WHERE FILE_NO = #{fileNo}
   AND BOARD_NO =#{boardNo}

/* 파일 삭제 */
UPDATE FILE_INFO
   SET DEL_YN = 'Y'
     , UPDATE_DATE = SYSDATE
 WHERE FILE_NO = #{fileNo}
   AND BOARD_NO =#{boardNo}
